SELECT CAST(`Free Meal Count (Ages 5-17)` AS REAL) / `Enrollment (Ages 5-17)` AS FreeRate 
FROM frpm 
WHERE `School Name` = 'Kacey Gibson' AND `Free Meal Count (Ages 5-17)` IS NOT NULL AND `Enrollment (Ages 5-17)` IS NOT NULL;

SELECT COUNT(*) 
FROM schools 
WHERE MailState = 'CA' AND MailCity = 'San Joaquin' AND StatusType = 'Active';

SELECT (COUNT(*) * 100.0 / (SELECT COUNT(*) FROM frpm WHERE `County Name` = 'Santa Clara')) AS percentage 
FROM frpm 
WHERE `County Name` = 'Santa Clara' AND `Charter Funding Type` = 'Locally funded';

SELECT CAST(SUM(CASE WHEN T.bond_type = '=' THEN 1 ELSE 0 END) AS REAL) * 100 / COUNT(T.bond_id) AS percent 
FROM bond AS T 
WHERE T.molecule_id = 'TR008';

SELECT COUNT(T1.bond_id) 
FROM connected AS T1 
WHERE T1.atom_id = 'TR%_19' OR T1.atom_id2 = 'TR%_19';

SELECT T1.atom_id2 FROM connected AS T1 
JOIN atom AS A ON T1.atom_id = A.atom_id 
WHERE A.element = 'pb';

SELECT ROUND(CAST(COUNT(CASE WHEN T.bond_type = '=' THEN T.bond_id END) AS REAL) * 100 / COUNT(T.bond_id), 5) 
FROM bond AS T 
WHERE T.molecule_id = 'TR047';

SELECT ROUND(CAST(COUNT(DISTINCT CASE WHEN T2.label = '+' AND T1.element = 'h' THEN T2.molecule_id ELSE NULL END) AS REAL) * 100 / COUNT(DISTINCT T2.molecule_id), 3) 
FROM atom AS T1 
INNER JOIN molecule AS T2 ON T1.molecule_id = T2.molecule_id;

SELECT T1.atom_id, T1.element 
FROM atom AS T1 
WHERE T1.molecule_id = 'TR186';

SELECT (COUNT(CASE WHEN convertedManaCost = 10 THEN 1 END) * 100.0 / COUNT(*)) AS percentage 
FROM cards 
WHERE setCode = 'Abyssal Horror';

SELECT DisplayName, (SELECT COUNT(Id) FROM badges WHERE UserId = T1.Id) AS BadgeCount 
FROM users AS T1 
WHERE Id = (SELECT UserId FROM comments GROUP BY UserId ORDER BY COUNT(Id) DESC LIMIT 1);

SELECT 
    (SELECT SUM(ViewCount) FROM posts WHERE OwnerUserId IN (SELECT Id FROM users WHERE DisplayName = 'Mornington')) - 
    (SELECT SUM(ViewCount) FROM posts WHERE OwnerUserId IN (SELECT Id FROM users WHERE DisplayName = 'Amos')) AS ViewCountDifference;

SELECT 
    (SELECT COUNT(*) FROM posts WHERE OwnerUserId = (SELECT Id FROM users ORDER BY CreationDate DESC LIMIT 1)) AS PostCount, 
    (SELECT COUNT(*) FROM comments WHERE UserId = (SELECT Id FROM users ORDER BY CreationDate DESC LIMIT 1)) AS CommentCount;

SELECT CAST(SUM(IIF(T1.Score > 50, 1, 0)) AS REAL) * 100 / COUNT(T1.Id) 
FROM posts AS T1 
WHERE T1.OwnerUserId = (SELECT Id FROM users WHERE Reputation = (SELECT MAX(Reputation) FROM users));

SELECT DISTINCT s.superhero_name 
FROM superhero AS s 
WHERE s.height_cm BETWEEN 170 AND 190 AND s.eye_colour_id IS NULL;

SELECT COUNT(DISTINCT s.id) 
FROM superhero AS s 
WHERE s.publisher_id IS NULL;

SELECT superhero_name 
FROM superhero 
WHERE height_cm = (SELECT MAX(height_cm) FROM superhero);

SELECT name 
FROM races 
WHERE year = 2017 AND raceId NOT IN (SELECT raceId FROM races WHERE year = 2000);

SELECT COUNT(DISTINCT driverId) 
FROM results 
WHERE raceId = 18;

SELECT forename || ' ' || surname AS full_name, dob 
FROM drivers 
WHERE nationality = 'Austrian' AND STRFTIME('%Y', dob) BETWEEN '1981' AND '1991';

SELECT SUM(T1.wins) 
FROM driverStandings AS T1 
WHERE T1.position = 91;

SELECT 
    (SELECT jumping FROM Player_Attributes WHERE player_api_id = 6) - 
    (SELECT jumping FROM Player_Attributes WHERE player_api_id = 23) AS jumping_difference;

SELECT CASE WHEN T1.`T-CHO` < 250 THEN 'Yes' ELSE 'No' END AS Cholesterol_Status 
FROM Laboratory AS T1 
WHERE T1.ID = 2927464 AND T1.Date = '1995-09-04';

SELECT P.Description, P.Diagnosis 
FROM Patient P 
WHERE P.ID = 48473;

SELECT COUNT(*) 
FROM Laboratory 
WHERE Date > '1997-01-01' AND APTT < 45;

SELECT T1.first_name || ' ' || T1.last_name AS full_name 
FROM member AS T1 
WHERE T1.t_shirt_size = 'X-Large';
